import React, { useState, useEffect } from 'react';
import { useHistory, useLocation } from "react-router-dom";
import axios from 'axios';

const initialState = {
    name: "",
    year: "",
    email: "",
    role: ""
};

const AddEdit = () => {
    const [state, setState] = useState(initialState);

    const { name, year, email, role } = initialState; // renamed initialState to State


    return (
        <div>
            <form action="">
                <label htmlFor="name">Name</label>
                <input type="text" id='name' name='name' placeholder='Enter name ...' onChange={handleInputChange} value={name} />
                <label htmlFor="year">Year</label>
                <input type="text" id='year' name='year' placeholder='Enter year ...' onChange={handleInputChange} value={year} />
                <label htmlFor="email">Email</label>
                <input type="text" id='email' name='email' placeholder='Enter email ...' onChange={handleInputChange} value={email} />
                <label htmlFor="role">Role</label>
                <input type="text" id='role' name='email' placeholder='Enter email ...' onChange={handleInputChange} value={email} />
            </form>
        </div>
    );
};

export default AddEdit;
