    const [state, setState] = useState(null);
    const { id } = useParams();
    const getSingleUser = async (id) => {
        const response = await axios.get(`http://localhost:5000/v1/user/${id}`);
        if (response.status === 200) {
            setState(response.data);
        }
    }
