import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
    const { activeTab, setActiveTab } = useState("Home");
    return (
        <header className="py-4 text-white bg-gray-800 a">
            <nav className="container flex items-center justify-between mx-auto">
                <p className="text-lg font-bold">User Management System</p>
                <div className="">
                    <Link to="/">
                        <div className={`${activeTab} === "Home" ? "active" : ""`} onClick={() => setActiveTab("Home")}>
                            <p>Home</p>
                        </div>
                    </Link>
                    <Link to="/add">
                        <div className={`${activeTab} === "AddUser" ? "active" : ""`} onClick={() => setActiveTab("AddUser")}>
                            <p>Add User</p>
                        </div>
                    </Link>
                    <Link to="/about">
                        <div className={`${activeTab} === "About" ? "active" : ""`} onClick={() => setActiveTab("About")}>
                            <p>About</p>
                        </div>
                    </Link>
                </div>
            </nav>
        </header>
    );
};

export default Header;
